package org.taktik.icure.services.external.rest.v2.dto.data;

/**
 * Created by aduchate on 31/07/13, 20:13
 */
public class GenericContent implements DisplayableContent {
	protected String entityId; //A patient id or a form id
	protected String entityClass; //Patient.class.canonicalName or Form.class.canonicalName
	protected String id;
	protected String label;

	public String getEntityId() {
		return entityId;
	}

	public void setEntityId(String entityId) {
		this.entityId = entityId;
	}

	public String getEntityClass() {
		return entityClass;
	}

	public void setEntityClass(String entityClass) {
		this.entityClass = entityClass;
	}

	public String getId() {
		return id;
	}

	public void setId(String id) {
		this.id = id;
	}

	public String getLabel() {
		return label;
	}

	public void setLabel(String label) {
		this.label = label;
	}
}
